import React, { useState } from "react";


const Admin = () => {
    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");
    const [isLoggedIn, setIsLoggedIn] = useState(false);

    const handleLogin = () => {
      if (username === "abc" && password === "abc@123") {
        setIsLoggedIn(true);
      } else {
        alert("Invalid username or password.");
      }
    };

    const handleLogout = () => {
      setIsLoggedIn(false);
    };

    const [contactUsData, setContactUsData] = useState(null);

    const storedData = localStorage.getItem('contact-us-data');
  const initialData = storedData ? JSON.parse(storedData) : null;

  useEffect(() => {
    setContactUsData(initialData);
  }, [initialData]);


    return (
      <div>
        {isLoggedIn ? (
          <div>
            <h2>Welcome, {username}!</h2>
            <h1>Dashboard</h1>
      {contactUsData && (
        <div>
          <h2>Contact Us Information</h2>
          <p>Name: {contactUsData.name}</p>
          <p>Email: {contactUsData.email}</p>
          <p>Message: {contactUsData.message}</p>
        </div>
      )}
            <button onClick={handleLogout}>Logout</button>
          </div>
        ) : (
          <div>
            <label>
              Username:
              <input
                type="text"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
              />
            </label>
            <br />
            <label>
              Password:
              <input
                type="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
              />
            </label>
            <br />
            <button onClick={handleLogin}>Login</button>
          </div>
        )}
      </div>
    );
  };

export default Admin
