import React, { useState, useEffect } from "react";

const Contact = () => {
  const [contactData, setContactData] = useState({});
  const [errorMsg, setErrorMsg] = useState();
  const [successMsg, setSuccessMsg] = useState(false);
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  const isValidEmail = (email) => {
    return /^[a-zA-Z0-9]+@[a-zA-Z0-9]+\.[A-Za-z]+$/.test(email);
  };

  const isValidMobile = (mobileno) => {
    return /^[6-9]\d{9}$/.test(mobileno);
  };

  const validateField = (field, value) => {
    if (value.length <= 0) {
      return (
        <>
          <span className="text-capitalize">{field}</span> is required field.
        </>
      );
    } else {
      if (field === "email") {
        if (!isValidEmail(value)) return "Invalid Email.";
      } else if (field === "mobile") {
        if (!isValidMobile(value)) return "Invalid Mobile Number.";
      } else {
        return "";
      }
    }
  };

  const handleBlur = (event) => {
    setErrorMsg(validateField(event.target.name, event.target.value));
  };

  const handleLogin = () => {
    if (username === "abc" && password === "abc@123") {
      setIsLoggedIn(true);
    } else {
      alert("Invalid username or password.");
    }
  };

  const handleLogout = () => {
    setIsLoggedIn(false);
  };

  const handleChange = (e) => {
    const { name, value } = e.target;

    setContactData({
      ...contactData,
      [name]: value,
    });

    if (name === "mobile") {
      setContactData({
        ...contactData,
        mobile: value.replace(/\D/, ""),
      });
    }
  };

  const handleSubmit = (e) => {
    let isValided = false;
    e.preventDefault();

    if (
      contactData.name === "" ||
      contactData.name === undefined 

    ) {
      setSuccessMsg(false);
      isValided = false;
    } else {
      setSuccessMsg(true);
      isValided = true;
    }
    return isValided;
  };

  const [contactUsData, setContactUsData] = useState(null);
  const storedData = localStorage.getItem("contact-us-data");
  const initialData = storedData ? JSON.parse(storedData) : null;
  useEffect(() => {
    setContactUsData(initialData);
  }, [initialData]);

  return (
    <div id="contact">
      <div className="form">
        <form
          onSubmit={handleSubmit}
          encType="multipart/form-data"
          autoComplete="off"
        >
          {!successMsg ? (
            <>
              <div id="errormessage" className={errorMsg ? "show" : ""}>
                {errorMsg}
              </div>
              <div className="form-group">
                <input
                  name="name"
                  type="text"
                  className="form-control"
                  placeholder="Name"
                  value={contactData.name || ""}
                  onChange={(e) => handleChange(e)}
                  onBlur={handleBlur}
                />
              </div>

              <div className="form-group">
                <textarea
                  name="message"
                  type="text"
                  className="form-control"
                  placeholder="Message"
                  value={contactData.message || ""}
                  onChange={(e) => handleChange(e)}
                  onBlur={handleBlur}
                  rows="3"
                />
              </div>
              <p className="text-right mb-0">
                <input
                  type="submit"
                  className="btn btn-primary"
                  value="Submit Now"
                />
              </p>
            </>
          ) : (
            <div className="show" id="sendmessage">
              {isLoggedIn ? (
                <div>
                  <h2>Welcome, {username}!</h2>
                  {contactUsData && (
                    <div>
                      <h3>Contact form data:</h3>
                      <p>
                        {contactData.name}
                        {contactData.message}
                      </p>
                    </div>
                  )}
                  <button onClick={handleLogout}>Logout</button>
                </div>
              ) : (
                <div>
                  <label>
                    Username:
                    <input
                      type="text"
                      value={username}
                      onChange={(e) => setUsername(e.target.value)}
                    />
                  </label>
                  <br />
                  <label>
                    Password:
                    <input
                      type="password"
                      value={password}
                      onChange={(e) => setPassword(e.target.value)}
                    />
                  </label>
                  <br />
                  <button onClick={handleLogin}>Login</button>
                </div>
              )}
            </div>
          )}
        </form>
      </div>
    </div>
  );
};

export default Contact;
